request_examples.javascript:
fetch('http://localhost:8000/standardize-address', {
  method: 'POST',
  headers: {'Content-Type': 'application/json'},
  body: JSON.stringify({address: 'Mẫn Xá, Long Châu, Yên Phong, Bắc Ninh'})
}).then(r => r.json()).then(console.log)

fetch('http://localhost:8000/standardize-address', {
  method: 'POST',
  headers: {'Content-Type': 'application/json'},
  body: JSON.stringify({address: 'Mẫn Xá, Long Châu, Yên Phong, Bắc Ninh', short_name: false})
}).then(r => r.json()).then(console.log)
